mapper.select:
SELECT trigger_stt_dtm,
       job_stt_dtm,
       job_end_dtm,
       trigger_group,
       trigger_name,
       job_group,
       job_name,
       job_exec_sta_cd,
       exec_prog_name,
       exec_prog_seq,
       job_exec_rslt
FROM tb_exec_history
WHERE 1=1

    AND trigger_stt_dtm = #{triggerSttDtm}


    AND trigger_group = #{triggerGroup}


    AND trigger_name = #{triggerName}


    AND job_group = #{jobGroup}


    AND job_name = #{jobName}


    AND exec_prog_name = #{execProgName}


    AND exec_prog_seq = #{execProgSeq}

SELECT job_stt_dtm, job_end_dtm, trigger_group, trigger_name, job_group, job_name, job_exec_sta_cd, exec_prog_name, job_exec_rslt FROM tb_exec_history WHERE 1=1
AND trigger_group = #{triggerGroup}
AND trigger_name = #{triggerName}
AND exec_prog_seq = #{seq}
ORDER BY trigger_stt_dtm DESC limit 1

mapper.update:

UPDATE tb_exec_history
SET job_stt_dtm = #{jobSttDtm},
    job_end_dtm = #{jobEndDtm},
    job_exec_sta_cd = #{jobExecStaCd},
    job_exec_rslt = #{jobExecRslt}
WHERE trigger_stt_dtm = #{triggerSttDtm}
AND trigger_group = #{triggerGroup}
AND trigger_name = #{triggerName}
AND job_group = #{jobGroup}
AND job_name = #{jobName}
AND exec_prog_seq = #{execProgSeq}

mapper.delete:

DELETE FROM tb_exec_history
WHERE job_stt_dtm = #{jobSttDtm}
  AND trigger_group = #{triggerGroup}
  AND trigger_name = #{triggerName}
  AND job_group = #{jobGroup}
  AND job_name = #{jobName}
