mapper.select:
select work_dvs_cd, prog_id, case when prog_dvs_cd = '00' then concat('SHELL(', prog_dvs_cd, ')') when prog_dvs_cd = '01' then concat('PC(', prog_dvs_cd, ')') when prog_dvs_cd = '02' then concat('ETC(', prog_dvs_cd, ')') end as prog_dvs_cd , prog_nm, prog_desc, prog_path, regist_dtime, regist_id, update_dtime, update_id from TBIBM713 where use_yn = 'Y'
and work_dvs_cd = #{workDvsCd}
order by work_dvs_cd asc, prog_id asc

select count(*)
  from TBIBM713
 where work_dvs_cd = #{workDvsCd}
